                            & NODE_PARAM2DOF_MAP%NODES(LOCAL_NODE_NUMBER)%NUMBER_OF_DERIVATIVES,"*",ERR,ERROR))//" derivatives."
                          CALL FLAG_ERROR(LOCAL_ERROR,ERR,ERROR,*999)
