      ENDIF
    ELSE
      LOCAL_ERROR="A region with an user number of "//TRIM(NUMBER_TO_VSTRING(RegionUserNumber,"*",Err,ERROR))// &
        & " does not exist."
      CALL FLAG_ERROR(LOCAL_ERROR,Err,ERROR,*999)
    ENDIF

#ifdef TAUPROF
